package array;

public class ForEachLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String name[]=new String[5];
		
		name[0]="Nayem";
		name[1]="muin";
		name[2]="Noman";
		name[3]="Poly";
		
		for(String y:name)
		{
			System.out.println(" "+y);
			
		}
		
		

	}

}
